#include <bits/stdc++.h>using namespace std;int main(){	int t;	cin >> t;	while (t--)	{		int n;		cin >> n;		int arr[n];		for (int i = 0; i < n; i++)		{			cin >> arr[i];		}		int max_jump = 1;		int start = 0;		while (start < n - 1)		{			int curr = arr[start];			int maxi = arr[start + 1];			if (curr == maxi)			{				start++;			}			else if (curr < maxi)			{				start++;			}			else			{				int pos = start + 1;				int currpos = start;				start++;				while (start < n - 1)				{					if (arr[start] > maxi)					{						maxi = arr[start];						pos = start;					}					start++;				}				max_jump = max(max_jump, pos - currpos);				start = pos;			}		}		if (is_sorted(arr, arr + n))		{			cout << n-1 << endl;		}		else			cout << max_jump << endl;	}}